def proc_ins_category(db_con, depth, low, cate_name, bcate_code, bcate_name, mcate_code, mcate_name):
    ins_catecode = ""
    ins_catename = ""
    rep_catename = cate_name.replace(' ','%20')
    cate_url = "https://www.dcbuy.co.kr/mall/search/" + str(rep_catename)
    # depth1 Insert

    sql_m = "select top 1 catecode, name from t_category where name = '{}' and catecode > 1000 and depth = {} order by regdate desc".format(cate_name, depth)
    row_m = db_con.selectone(sql_m)
    if row_m:
        ins_catecode = row_m[0]
        ins_catename = row_m[1]
        print(">> 존재하는 카테고리 [dtpth:{}] ins_catecode : {} | {} ".format(depth, ins_catecode, ins_catename))
    else:
        dic = dict()
        dic['name'] = "'" + cate_name + "'"
        dic['kor_name'] = "'" + cate_name + "'"
        dic['depth'] = depth
        dic['sort'] = low
        dic['amz_cateurl'] = "'" + str(cate_url) + "'"
        dic['cate_keyword'] = "'" + str(rep_catename) + "'"

        if depth == 1:
            dic['big'] = "'" + cate_name + "'"

        elif depth == 2:
            dic['parent'] = bcate_code
            dic['bcate'] = bcate_code
            dic['big'] = "'" + bcate_name + "'"
            dic['middle'] = "'" + cate_name + "'"

        elif depth == 3:
            dic['parent'] = mcate_code
            dic['bcate'] = bcate_code
            dic['mcate'] = mcate_code
            dic['big'] = "'" + bcate_name + "'"
            dic['middle'] = "'" + mcate_name + "'"
            dic['small'] = "'" + cate_name + "'"
            dic['lastcate'] = "'1'"

        print('New Category')
        print('##insert## : t_category : {}'.format(dic))
        db_con.insert('t_category', dic)  # insert
        time.sleep(0.2)

        sql = " select top 1 catecode, name from t_category where name = '{}' order by regdate desc".format(cate_name)
        row = db_con.selectone(sql)
        if row:
            ins_catecode = row[0]
            ins_catename = row[1]
            print(">> [dtpth:{}] ins_catecode : {} | {} ".format(depth, ins_catecode, ins_catename))

    return ins_catecode, ins_catename

